
public class both {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n= 6;
		if (n>0)
		{   
	    	   System.out.println(n+ "  is positive number");
		}  
	       else if (n<0)
	       {  
	    	   System.out.println(n + " is negative ");
	       } 
		
		if (n%2==0)
		System.out.println(n+"  is even  ");
		else
			System.out.println(n+"  is odd  ");
	}

}
